GridScript = GridScript or { }function GridScript:OnCreate()	SubscribeToEvent("MouseDown", self.object)	SubscribeToEvent("EveryFrame", self.object)endfunction GridScript:OnMouseDown(event)	newball = Create("BallProto", "")	Attach(self.object, newball)	SetPosition(newball, Random_Int(0, 288), Random_Int(0, 288))endfunction GridScript:OnEveryFrame()end