#x432;&#x438;&#x436;&#x435;&#x43d;&#x438;&#x44f; &#x433;&#x43b;&#x430;&#x432;&#x43d;&#x43e;&#x433;&#x43e; &#x433;&#x435;&#x440;&#x43e;&#x44f;. &quot;&#x421;&#x43b;&#x43e;&#x436;&#x43d;&#x430;&#x44f; &#x434;&#x438;&#x43d;&#x430;&#x43c;&#x438;&#x43a;&#x430; &#x43f;&#x435;&#x440;&#x435;&#x434;&#x432;&#x438;&#x436;&#x435;&#x43d;&#x438;&#x44f;&quot; - &#x442;&#x440;&#x435;&#x431;&#x443;&#x435;&#x442; &#x431;&#x43e;&#x43b;&#x435;&#x435; &#x43f;&#x43e;&#x434;&#x440;&#x43e;&#x431;&#x43d;&#x43e;&#x433;&#x43e; &#x43e;&#x431;&#x44a;&#x44f;&#x441;&#x43d;&#x435;&#x43d;&#x438;&#x44f;, &#x43d;&#x43e; &#x44f; &#x435;&#x433;&#x43e; &#x43e;&#x43f;&#x443;&#x449;&#x443; &#x437;&#x434;&#x435;&#x441;&#x44c;. &#x414;&#x43e;&#x441;&#x442;&#x430;&#x442;&#x43e;&#x447;&#x43d;&#x43e; &#x43f;&#x440;&#x43e;&#x441;&#x442;&#x43e; &#x43f;&#x43e;&#x441;&#x43c;&#x43e;&#x442;&#x440;&#x435;&#x442;&#x44c; &#x432; &#x441;&#x43f;&#x43e;&#x441;&#x43e;&#x431;&#x43d;&#x43e;&#x441;&#x442;&#x438;: &#x433;&#x433; &#x43c;&#x43e;&#x436;&#x435;&#x442; &#x43e;&#x442;&#x442;&#x430;&#x43b;&#x43a;&#x438;&#x432;&#x430;&#x442;&#x44c;&#x441;&#x44f; &#x43e;&#x442; &#x441;&#x442;&#x435;&#x43d;, &#x434;&#x435;&#x43b;&#x430;&#x442;&#x44c; &#x43f;&#x43e;&#x434;&#x43a;&#x430;&#x442;&#x44b;, &#x446;&#x435;&#x43f;&#x43b;&#x44f;&#x442;&#x44c;&#x441;&#x44f; &#x437;&#x430; &#x43f;&#x43e;&#x442;&#x43e;&#x43b;&#x43e;&#x43a;, &#x43d;&#x430; &#x445;&#x430;&#x440;&#x430;&#x43a;&#x442;&#x435;&#x440;&#x438;&#x441;&#x442;&#x438;&#x43a;&#x438; &#x43f;&#x435;&#x440;&#x435;&#x434;&#x432;&#x438;&#x436;&#x435;&#x43d;&#x438;&#x44f; &#x432;&#x43b;&#x438;&#x44e;&#x442; &#x442;&#x435;&#x43a;&#x443;&#x449;&#x430;&#x44f; &#x441;&#x43a;&#x43e;&#x440;&#x43e;&#x441;&#x442;&#x44c; &#x43f;&#x435;&#x440;&#x441;&#x43e;&#x43d;&#x430;&#x436;&#x430;; &#x435;&#x441;&#x442;&#x44c; &#x442;&#x430;&#x43a;&#x43e;&#x435; &#x43f;&#x43e;&#x43d;&#x44f;&#x442;&#x438;&#x435; &#x43a;&#x430;&#x43a; &quot;&#x440;&#x430;&#x437;&#x431;&#x435;&#x433;&quot; &#x438;&#x442;&#x434;. &#x412;&#x43e;-&#x432;&#x442;&#x43e;&#x440;&#x44b;&#x445;, &#x433;&#x43e;&#x43b;&#x43e;&#x432;&#x43e;&#x43b;&#x43e;&#x43c;&#x43a;&#x438;. &#x41f;&#x443;&#x43d;&#x43a;&#x442; &#x43f;&#x440;&#x43e; &#x433;&#x43e;&#x43b;&#x43e;&#x432;&#x43e;&#x43b;&#x43e;&#x43c;&#x43a;&#x438; &#x435;&#x449;&#x451; &#x43d;&#x435; &#x43f;&#x440;&#x43e;&#x434;&#x443;&#x43c;&#x430;&#x43d;. &#x412;-&#x442;&#x440;&#x435;&#x442;&#x44c;&#x438;&#x445;, &#x434;&#x438;&#x430;&#x43b;&#x43e;&#x433;&#x438; &#x441; &#x43f;&#x435;&#x440;&#x441;&#x43e;&#x43d;&#x430;&#x436;&#x430;&#x43c;&#x438;, &#x432;&#x43e;&#x437;&#x43c;&#x43e;&#x436;&#x43d;&#x43e;&#x441;&#x442;&#x44c; &#x441; &#x43d;&#x438;&#x43c;&#x438; &#x433;&#x43e;&#x432;&#x43e;&#x440;&#x438;&#x442;&#x44c; &#x438;&#x442;&#x434; &#x434;&#x43e;&#x43b;&#x436;&#x43d;&#x430; &#x441;&#x443;&#x449;&#x435;&#x441;&#x442;&#x432;&#x43e;&#x432;&#x430;&#x442;&#x44c;. &#x42d;&#x442;&#x43e; &#x431;&#x443;&#x434;&#x435;&#x442; &#x441;&#x440;&#x435;&#x434;&#x441;&#x442;&#x432;&#x43e; &#x434;&#x43b;&#x44f; &#x440;&#x430;&#x441;&#x43a;&#x440;&#x44b;&#x442;&#x438;&#x44f; &#x441;&#x44e;&#x436;&#x435;&#x442;&#x430;. &#x412;-&#x447;&#x435;&#x442;&#x432;&#x451;&#x440;&#x442;&#x44b;&#x445;, &#x432; &#x438;&#x433;&#x440;&#x435; &#x431;&#x443;&#x434;&#x443;&#x442; &#x438;&#x441;&#x43f;&#x43e;&#x43b;&#x44c;&#x437;&#x43e;&#x432;&#x430;&#x43d;&#x44b; &#x442;&#x430;&#x43a;&#x438;&#x435; &#x43f;&#x440;&#x438;&#x451;&#x43c;&#x44b; &#x43a;&#x430;&#x43a; #00 &#x438; #01. &#x41f;&#x43e;&#x437;&#x436;&#x435; &#x44f; &#x43f;&#x43e;&#x43f;&#x440;&#x43e;&#x431;&#x443;&#x44e; &#x43d;&#x430;&#x439;&#x442;&#x438; &#x44d;&#x442;&#x438; &#x442;&#x435;&#x440;&#x43c;&#x438;&#x43d;&#x44b; &#x432; &#x433;&#x443;&#x433;&#x43b;&#x435;. &#x42f; &#x43f;&#x43e;&#x43d;&#x44f;&#x442;&#x438;&#x435; &#x43d;&#x435; &#x438;&#x43c;&#x435;&#x44e; &#x43a;&#x430;&#x43a; &#x44d;&#x442;&#x43e; &#x441;&#x43a;&#x430;&#x437;&#x430;&#x442;&#x44c; &#x438; &#x43f;&#x440;&#x435;&#x434;&#x43f;&#x43e;&#x43b;&#x430;&#